mapper.select:
    select  from cobra_apply_store_detail
    	
			
				yjs_user_id = #{yjsUserId}
			
			
				and apply_status = #{applyStatus} 
			
			order by created_date desc
			limit #{start,jdbcType=INTEGER}, #{end}

    select  from cobra_apply_store_detail
    	
			
				yjs_user_id = #{yjsUserId}
			
			
				and apply_status = #{applyStatus} 
				or apply_status=3
			
			order by created_date desc
			limit #{start,jdbcType=INTEGER}, #{end}

  		 select
		count(1)
		 from cobra_apply_store_detail
    	
			
				yjs_user_id = #{yjsUserId}
			
			
				and apply_status = #{applyStatus}

    select 
    
    from cobra_apply_store
   		 
			
				apply_type = #{applyType}
			
			
				and apply_status = #{applyStatus}
			
			
				and finance_check = #{financeCheck}

    select  from cobra_apply_store
    	
			
				yjs_user_id = #{yjsUserId}
			
			
				and apply_status = #{applyStatus}
			
			order by created_date desc
			limit #{start,jdbcType=INTEGER}, #{end}

    select  from cobra_apply_store
    	
			
				who_check = #{whoCheck}
			
			
				and apply_status = #{applyStatus}
			
			
				and whether_start_apply = #{whetherStartApply}
			
			order by created_date desc
			limit #{start,jdbcType=INTEGER}, #{end}
